namespace AdventOfCode2022
{
    internal class Day6 : Day
    {

        public override void Run()
        {
            int answer = 0;

            Queue<char> queue = new Queue<char>();
            for (int i = 0; i < input.Length; i++)
            {
                // enter unknown state
                queue.Enqueue(input[i]);
                answer++;

                if (queue.Count == 15) // next cycle remove oldest 
                {
                    queue.Dequeue();

                    Queue<char> thisSet = new Queue<char>(queue.ToList());
                    bool hadADupe = false;
                    // for all chars
                    while (thisSet.Count > 0)
                    {
                        // if there is a copy
                        char c = thisSet.Dequeue();
                        if (thisSet.Contains(c))
                        {
                            hadADupe = true;
                        }
                    }

                    if (!hadADupe)
                    {
                        break;
                    }
                }
               
            }

            Console.WriteLine("The answer is {0}", answer);
        }

        string input = "";

        // Copy / paste input string in the quotes
    }
}
